import React, { useState } from "react";
import axios from "axios";
import { setUserSession } from "./Utils/Common";
import { Pane, TextInputField, Heading, Button, BuildIcon } from "evergreen-ui";
import logo1 from "./assets/img/logo1.png";

import IndexNavbar from "./components/Navbars/IndexNavbar2.js";
import Footer from "./components/Footers/Footer";

import swal from "sweetalert";

function Register(props) {
  const username = useFormInput("");
  const password = useFormInput("");
  const kadpengenalan = useFormInput("");
  const email = useFormInput("");
  const contactno = useFormInput("");
  const address = useFormInput("");
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(false);

  const handleRegister = () => {
    setError(null);
    setLoading(true);

    // axios
    //   .post("https://apisim.mps.gov.my/api/mymps/akaunbyic?nokp=930420145231", {
    //     username: username.value,
    //     password: password.value,
    //   })
    //   .then((response) => {
    //     setLoading(false);
    //     setUserSession(response.data[0].NOAKAUN, response.data[0].NAMA_PEMILIK);

    //     console.log(response.data);
    //     props.history.push("/dashboard");
    //   })
    //   .catch((error) => {
    //     setLoading(false);

    //     console.log("Something went wrong. Plese try again!");
    //     if (error.response.status === 401)
    //       setError(error.response.data.message);
    //     else setError("Something went wrong. Please try again later.");
    //   });

    if (username.value == "" || password.value == "") {
      swal(
        "Opss!",
        "Sila pastikan kata nama dan kata laluan anda sah",
        "error"
      );
      setLoading(false);
    } else {
      var myHeaders = new Headers();
      myHeaders.append(
        "Cookie",
        "__cfduid=db6f29fff3ce79e0a3fe70a3990b3baeb1603864221; CORRAD_LANGUAGE=1; CORRAD_THEME=lavender; CORRAD_API_MYMPS=6nghhhcstuc4aad81r3m9qqp4b; MYMPS_API=jstp68stuf7siabrp233iff4pt"
      );

      var formdata = new FormData();
      formdata.append("username", username.value);
      formdata.append("password", password.value);

      var requestOptions = {
        method: "POST",
        body: formdata,
        redirect: "follow",
      };

      var urlAPI1 =
        "https://mymps.corrad.my/int/api_generator.php?api_name=api_login";
      var urlAPI2 = "https://api.corrad.my/api/API-login";

      fetch(urlAPI1, requestOptions)
        .then((response) => response.json())
        .then((result) => {
          //console.log(result);
          setLoading(false);

          if (result.status == "unsuccess") {
            console.log("Wrong credentials. Please try again!");
            swal(
              "Opss!",
              "Sila pastikan kata nama dan kata laluan anda sah",
              "error"
            );
          } else if (result.status == "success") {
            setUserSession(result.data[0].TOKEN, result.data[0].USERNAME);

            swal("Bejaya!", "Login Berjaya!", "success");
            props.history.push("/dashboard");
          }
        })
        .catch((error) => {
          console.log(error);
          swal(
            "Opss!",
            "Something went wrong. Please contact your administrator!",
            "error"
          ).then((value) => {
            props.history.push("/");
          });
        });
    }
  };

  const handleNumber = () => {
    console.log(username.value);
  };

  return (
    <div className="bg-gray">
      <IndexNavbar fixed />
      {/* Login <br /><br /> */}
      {/* <div>
                Username <br />
                <input type="text" {...username} />
            </div>
            <div style={{ marginTop: 10 }}>
                Password <br />
                <input type="password" {...password} />
            </div>
            {error && <><small style={{ color: 'red' }}>{error}</small><br /></>}<br />
            <input type="button" value={loading ? 'Loading...' : 'Login'} onClick={handleLogin} disabled={loading} /><br /> */}
      <section
        className="py-20 bg-gray-700 overflow-hidden h-100"
        style={{ height: "30vh" }}
      ></section>
      <section className="pb-16 bg-gray-300 relative pt-32">
        <div className="container mx-auto">

          <div className="flex flex-wrap justify-center bg-white shadow-2xl rounded-lg -mt-64 py-16 px-10 relative z-10">
            <div className="w-full text-center lg:w-4/9">
              <p className="text-center">
                <img
                  className="mx-auto"
                  src={logo1}
                  style={{ height: "150px", width: "150px" }}
                ></img>
              </p>
              <Heading size={700} marginTop="default">
                Pendaftaran Pengguna
              </Heading>
              {/* <p className="text-gray-600 text-lg leading-relaxed mt-4 mb-4">
                        Cause if you do, it can be yours now. Hit the buttons below to
                        navigate to get the Free version for your next project. Build a
                        new web app or give an old project a new look!
                    </p> */}
              <div className="sm:block flex flex-col mt-20">
                <div className="text-left p-5">
                  <div className="row">
                    <div className="col-md-5">
                      <TextInputField
                        padding={5}
                        label="KATA PENGGUNA"
                        //hint="This is a hint."
                        placeholder="cth: Taufik"
                        {...username}
                        width="50%"
                      />
                    </div>
                    <div className="col-md-5">
                      <TextInputField
                        padding={5}
                        label="KATA LALUAN"
                        //hint="This is a hint."
                        placeholder="cth: abcd1234@^"
                        type="password"
                        {...password}
                        width="50%"
                      />
                    </div>
                  </div>
                  <Pane
                    display="flex"
                    alignItems="center"
                    justifyContent="center"
                    padding={5}
                    margin={3}
                  >
                    <TextInputField
                      padding={5}
                      label="KATA PENGGUNA"
                      //hint="This is a hint."
                      placeholder="cth: Taufik"
                      {...username}
                      width="50%"
                    />

                    <TextInputField
                      padding={5}
                      label="KATA LALUAN"
                      //hint="This is a hint."
                      placeholder="cth: abcd1234@^"
                      type="password"
                      {...password}
                      width="50%"
                    />
                  </Pane>

                  <TextInputField
                    label="NO. KAD PENGENALAN"
                    //hint="This is a hint."
                    placeholder="cth: 203451057890"
                    onKeyUp={handleNumber}
                    {...kadpengenalan}
                  />

                  <TextInputField
                    label="EMEL PENGGUNA"
                    //hint="This is a hint."
                    placeholder="cth: Taufik"
                    {...email}
                  />

                  <TextInputField
                    label="NO. TELEFON"
                    //hint="This is a hint."
                    placeholder="cth: Taufik"
                    {...contactno}
                  />

                  <TextInputField
                    label="ALAMAT"
                    //hint="This is a hint."
                    placeholder="cth: abcd1234@^"
                    type="password"
                    {...address}
                  />
                </div>

                <div className="top-29-px">
                  <Button
                    marginRight={16}
                    appearance="primary"
                    intent="success"
                    value={loading ? "Loading..." : "Daftar"}
                    onClick={handleRegister}
                    disabled={loading}
                  >
                    Daftar Pengguna
                  </Button>

                  <Button
                    marginRight={16}
                    appearance="primary"
                    intent="none"
                    onClick={() => (window.location.href = "/login")}
                  >
                    Login
                  </Button>
                </div>
              </div>
              <div className="text-center mt-16"></div>
            </div>
          </div>
        </div>
      </section>
      <Footer />
    </div>
  );
}

const useFormInput = (initialValue) => {
  const [value, setValue] = useState(initialValue);

  const handleChange = (e) => {
    setValue(e.target.value);
  };
  return {
    value,
    onChange: handleChange,
  };
};

export default Register;
