import React, { useState } from "react";
import axios from "axios";
import { setUserSession } from "./Utils/Common";
import { Pane, TextInputField, Heading, Button, BuildIcon } from "evergreen-ui";
import logo1 from "./assets/img/logo1.png";

import IndexNavbar from "./components/Navbars/IndexNavbar2.js";
import Footer from "./components/Footers/Footer";

function Register(props) {
  const username = useFormInput("");
  const password = useFormInput("");
  const [error, setError]     = useState(null);
  const [loading, setLoading] = useState(false);

  const handleLogin = () => {
    setError(null);
    setLoading(true);

    axios
      .post("https://apisim.mps.gov.my/api/mymps/akaunbyic?nokp=930420145231", {
        username: username.value,
        password: password.value,
      })
      .then((response) => {
        setLoading(false);
        setUserSession(response.data[0].NOAKAUN, response.data[0].NAMA_PEMILIK);

        console.log(response.data);
        props.history.push("/dashboard");
      })
      .catch((error) => {
        setLoading(false);

        console.log("Something went wrong. Plese try again!");
        if (error.response.status === 401)
          setError(error.response.data.message);
        else setError("Something went wrong. Please try again later.");
      });
  };

  const handleNumber = () => {
    console.log(username.value);
  };

  return (
    <div className="bg-gray">
      <IndexNavbar fixed />


      <section className="py-20 relative" style={{background: "rgb(34,81,122)", background: "linear-gradient(90deg, rgba(34,81,122,1) 0%, rgba(27,147,171,1) 100%)"}}>

      <div class="w-full">
        <div class="h-2 bg-indigo"></div>
          <div class="flex items-center justify-center h-screen">
            <div class="container mx-24 bg-white rounded shadow-lg">
              <div class="px-12 py-6">
                <div class="text-center">
                  <h1 class="font-normal text-3xl text-grey-darkest leading-loose my-3 w-full">Become a Stripe Partner today</h1>
                  <div class="w-full text-center">
                    <form action="#">
                      <div class="max-w-sm mx-auto p-1 pr-0 flex items-center">
                        <input type="email" placeholder="jenny.rosen@example.com" class="flex-1 appearance-none rounded shadow p-3 text-grey-dark mr-2 focus:outline-none" />
                        <button type="submit" class="appearance-none bg-indigo-500 text-white text-base font-semibold tracking-wide uppercase p-3 rounded shadow hover:bg-indigo-light">Get started</button>
                      </div>
                    </form>
                  </div>
                </div>
              </div>
            </div>
          </div>
      </div>

      <div class="flex items-center h-screen w-full bg-teal-100">
        <div class="w-full bg-white rounded shadow-xl p-8 m-4 md:max-w-sm md:mx-auto">
          <h1 class="block w-full text-center text-grey-darkest mb-6"><Heading size={700} marginTop="default">Login MyMPS</Heading></h1>

          <form class="mb-4 lg:flex lg:flex-wrap lg:justify-between">
            <div class="flex flex-col mb-4 lg:w-1/2">
              {/* <label class="mb-2 uppercase tracking-wide font-bold text-lg text-grey-darkest" for="first_name">First Name</label>
              <input class="border py-2 px-3 text-grey-darkest lg:mr-2" type="text" name="first_name" id="first_name" /> */}
              <TextInputField
                label="Kata Pengguna"
                hint="This is a hint."
                placeholder="Placeholder text"
                width="100%"
                padding={5}
              />
            </div>
            <div class="flex flex-col mb-4 md:w-1/2">
              <label class="mb-2 uppercase font-bold text-lg text-grey-darkest md:ml-2" for="last_name">Last Name</label>
              <input class="border py-2 px-3 text-grey-darkest md:ml-2" type="text" name="last_name" id="last_name" />
            </div>
            <div class="flex flex-col mb-4 md:w-full">
              <label class="mb-2 uppercase font-bold text-lg text-grey-darkest" for="email">Email</label>
              <input class="border py-2 px-3 text-grey-darkest" type="email" name="email" id="email" />
            </div>
            <div class="flex flex-col mb-6 md:w-full">
              <label class="mb-2 uppercase font-bold text-lg text-grey-darkest" for="password">Password</label>
              <input class="border py-2 px-3 text-grey-darkest" type="password" name="password" id="password" />
            </div>
            <button class="block bg-teal hover:bg-teal-dark text-white uppercase text-lg mx-auto p-4 rounded" type="submit">Create Account</button>
          </form>

          <div className="mx-auto">
          <Button
                    marginRight={16}
                    appearance="primary"
                    intent="none"
                    onClick={() => (window.location.href = "/login")}
                    className="mx-auto"
                  >
                    Already have an account?
                  </Button>
                  </div>
        </div>
      </div>

      {/* <div className="container mx-auto" style={{marginTop: "22%"}}>
          <div className="flex flex-wrap justify-center bg-white shadow-2xl rounded-lg -mt-64 py-16 px-10 relative z-10">
            <div className="w-full text-center lg:w-8/12">
              <p className="text-center">
                <img
                  className="mx-auto"
                  src={logo1}
                  style={{ height: "150px", width: "150px" }}
