import React, { useState } from "react";
import axios from "axios";
import { setUserSession } from "./Utils/Common";
import { Pane, TextInputField, Heading, Button, BuildIcon } from "evergreen-ui";
import logo1 from "./assets/img/logo1.png";

import IndexNavbar from "./components/Navbars/IndexNavbar2.js";
import Footer from "./components/Footers/Footer";

import swal from "sweetalert";

function Register(props) {
  const username = useFormInput("");
  const password = useFormInput("");
  const kadpengenalan = useFormInput("");
  const email = useFormInput("");
  const contactno = useFormInput("");
  const address = useFormInput("");
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(false);

  const handleRegister = () => {
    setError(null);
    setLoading(true);

    // axios
    //   .post("https://apisim.mps.gov.my/api/mymps/akaunbyic?nokp=930420145231", {
    //     username: username.value,
    //     password: password.value,
    //   })
    //   .then((response) => {
    //     setLoading(false);
    //     setUserSession(response.data[0].NOAKAUN, response.data[0].NAMA_PEMILIK);

    //     console.log(response.data);
    //     props.history.push("/dashboard");
    //   })
    //   .catch((error) => {
    //     setLoading(false);

    //     console.log("Something went wrong. Plese try again!");
    //     if (error.response.status === 401)
    //       setError(error.response.data.message);
    //     else setError("Something went wrong. Please try again later.");
    //   });

    if (username.value == "" || password.value == "") {
      swal(
        "Opss!",
        "Sila pastikan kata nama dan kata laluan anda sah",
        "error"
      );
      setLoading(false);
    } else {
      var myHeaders = new Headers();
      myHeaders.append(
        "Cookie",
        "__cfduid=db6f29fff3ce79e0a3fe70a3990b3baeb1603864221; CORRAD_LANGUAGE=1; CORRAD_THEME=lavender; CORRAD_API_MYMPS=6nghhhcstuc4aad81r3m9qqp4b; MYMPS_API=jstp68stuf7siabrp233iff4pt"
      );

      var formdata = new FormData();
      formdata.append("username", username.value);
      formdata.append("password", password.value);

      var requestOptions = {
        method: "POST",
        body: formdata,
        redirect: "follow",
      };

      var urlAPI1 =
        "https://mymps.corrad.my/int/api_generator.php?api_name=api_login";
      var urlAPI2 = "https://api.corrad.my/api/API-login";

      fetch(urlAPI1, requestOptions)
        .then((response) => response.json())
        .then((result) => {
          //console.log(result);
          setLoading(false);

          if (result.status == "unsuccess") {
            console.log("Wrong credentials. Please try again!");
            swal(
              "Opss!",
              "Sila pastikan kata nama dan kata laluan anda sah",
              "error"
            );
          } else if (result.status == "success") {
            setUserSession(result.data[0].TOKEN, result.data[0].USERNAME);

            swal("Bejaya!", "Login Berjaya!", "success");
            props.history.push("/dashboard");
          }
        })
        .catch((error) => {
          console.log(error);
          swal(
            "Opss!",
            "Something went wrong. Please contact your administrator!",
            "error"
          ).then((value) => {
            props.history.push("/");
          });
        });
    }
  };

  const handleNumber = () => {
    console.log(username.value);
  };

  return (
    <div className="bg-gray">
      <IndexNavbar fixed />
      {/* Login <br /><br /> */}
      {/* <div>
                Username <br />
                <input type="text" {...username} />
            </div>
            <div style={{ marginTop: 10 }}>
                Password <br />
                <input type="password" {...password} />
            </div>
            {error && <><small style={{ color: 'red' }}>{error}</small><br /></>}<br />
            <input type="button" value={loading ? 'Loading...' : 'Login'} onClick={handleLogin} disabled={loading} /><br /> */}
      <section className="py-20 bg-gray-700 overflow-hidden h-100 p-5 mt-5">
      <div class="max-w-sm rounded overflow-hidden shadow-lg bg-white p-5 mx-auto" style={{width: "90%"}}>
        <div className="mx-auto">
          <img className="mx-auto mt-3" src={logo1} height="180px" width="180px"></img>
          <div class="px-6 py-4 text-center mt-6">
            <div class="font-bold text-xl mb-2">Pendaftaran Pengguna</div>
            <p class="text-gray-700 text-base">
              Selamat Datang ke Laman Pendaftaran Pengguna Portal Rasmi Majlis Perbandaran Selayang 2020.
            </p>
          </div>
        </div>
        <div class="px-6 pt-4 pb-2">
          {/* <span class="inline-block bg-gray-200 rounded-full px-3 py-1 text-sm font-semibold text-gray-700 mr-2 mb-2">#photography</span>
          <span class="inline-block bg-gray-200 rounded-full px-3 py-1 text-sm font-semibold text-gray-700 mr-2 mb-2">#travel</span>
          <span class="inline-block bg-gray-200 rounded-full px-3 py-1 text-sm font-semibold text-gray-700 mr-2 mb-2">#winter</span> */}
        </div>

        {/* form start */}
        <form class="w-full max-w-lg mt-6">
          <div class="flex flex-wrap -mx-3 mb-6">

            <div class="w-full md:w-1/2 px-3 mb-6 md:mb-0">
              <label class="block uppercase tracking-wide text-gray-700 text-xs font-bold mb-2" for="grid-first-name">
                First Name
              </label>
              <input class="appearance-none block w-full bg-gray-200 text-gray-700 border border-red-500 rounded py-3 px-4 mb-3 leading-tight focus:outline-none focus:bg-white" id="grid-first-name" type="text" placeholder="Jane" />
              {/* <p class="text-red-500 text-xs italic">Please fill out this field.</p> */}
            </div>

            <div class="w-full md:w-1/2 px-3">
              <label class="block uppercase tracking-wide text-gray-700 text-xs font-bold mb-2" for="grid-last-name">
                Last Name
              </label>
              <input class="appearance-none block w-full bg-gray-200 text-gray-700 border border-gray-200 rounded py-3 px-4 leading-tight focus:outline-none focus:bg-white focus:border-gray-500" id="grid-last-name" type="text" placeholder="Doe" />
            </div>

          </div>

          <div class="flex flex-wrap -mx-3 mb-6">
            <div class="w-full px-3">
              <label class="block uppercase tracking-wide text-gray-700 text-xs font-bold mb-2" for="grid-password">
                Password
              </label>
              <input class="appearance-none block w-full bg-gray-200 text-gray-700 border border-gray-200 rounded py-3 px-4 mb-3 leading-tight focus:outline-none focus:bg-white focus:border-gray-500" id="grid-password" type="password" placeholder="******************" />
              <p class="text-gray-600 text-xs italic">Make it as long and as crazy as you'd like</p>
            </div>
          </div>

          <div class="flex flex-wrap -mx-3 mb-2">
            <div class="w-full md:w-1/3 px-3 mb-6 md:mb-0">
              <label class="block uppercase tracking-wide text-gray-700 text-xs font-bold mb-2" for="grid-city">
                City
              </label>
              <input class="appearance-none block w-full bg-gray-200 text-gray-700 border border-gray-200 rounded py-3 px-4 leading-tight focus:outline-none focus:bg-white focus:border-gray-500" id="grid-city" type="text" placeholder="Albuquerque" />
            </div>
            <div class="w-full md:w-1/3 px-3 mb-6 md:mb-0">
