import Axios from 'axios';
import React, {useState, useEffect} from 'react'
import swal from 'sweetalert'
import { getUser, getNOKP, getToken, removeUserSession } from "./Utils/Common";

export default function Carian({data,bill}){

    const [loading,setLoading] = useState(false);
    const nokp    = getNOKP();
    let type = "";
        // + something something something
        // let formdata = new FormData();
        // formdata.append('id',id.value)
        // axios.post('https://mymps.corrad.my/int/api_generator.php?api_name=newBill',formdata)
        // .then(response=>{

        //     if(response.status == "success"){
        //         console.log(response.status)
        //         swal('Berjaya Tambah','Berjaya tambah akaun untuk pembayaran','success')
        //         window.location.href = '/bill';
        //     }
        //     else{
        //         swal('Tidak Berjaya','No Akaun tidak dijumpai','error')
        //     }
        // })
        // .catch(err => {errors: err.response});

        // ada bill masuk bill list xde error
        // console.log('Carian: '+ props.data.search);
        useEffect(() => {
            if(data.type === 'nokp')
                type = 'No Kad Pengenalan';
            if(data.type === 'akaun')
                type = 'Akaun';
            if(data.type === 'ssm')
                type = 'No SSM';
        },[data])

        const handleAdd = (e) => {
            setLoading(true);
            const formData = new FormData();
            formData.append('nokp',nokp);
            formData.append('account',bill.noakaun);
            console.log(nokp);
            console.log(bill.noakaun);
            Axios.post('https://mymps.corrad.my/int/api_generator.php?api_name=newBill',formData)
            .then(res => {


                if(res.data.status === "success"){
                    swal('Berjaya Tambah','Berjaya tambah akaun untuk pembayaran','success');
                    window.location.href = '/bill';
                }
                else if(res.data.status === "failure"){
                    swal("Tidak Berjaya",type+" ini telah didaftarkan ke akaun anda.","error");
                }
                else{
                    console.log(res.status);
                    swal('Ralat','Operasi tidak dapat diselesaikan','error');
                }

                setLoading(false);
            })
            .catch(err =>{
                console.log('error',err)
                swal('Ralat','Sila hubungi pentadbir sistem!','error');
            });
        }
    
    if( data.search == ''){
        return (
            <div>
            </div>
        );
    }
    else{
        return (
            <div>
                <div className="px-4 md:px-2 mx-auto w-full">
                    <div className="flex flex-wrap">
                        <div className="w-full px-4">
                            <div className="relative flex flex-col min-w-0 break-words bg-white rounded mb-6 shadow-lg">
                                <div className="flex-auto p-4">
                                    <div className="flex flex-row pt-4">
                                        <div className="relative w-full pr-4 max-w-full flex-grow flex-1">
                                            <span className="font-semibold uppercase text-lg text-gray-800">
                                            {
                                            // dataset.jenis
                                            // bill.type
                                            bill.noakaun
                                            }
                                            </span>	
                                        </div>
                                        <div className="relative w-auto pl-4 flex-initial">
                                            <span className="font-semibold uppercase text-lg text-gray-800">
                                            {
                                                //dataset.amaun
                                                // bill.amaun
                                            }
                                            </span>	
                                        </div>
                                    </div>
                                    <div className="flex flex-row pb-4">
                                        <div className="relative w-full pr-4 max-w-full flex-grow flex-1">
                                            <span className="font-semibold uppercase text-lg text-gray-800">
                                            {
                                            // dataset.akaun
                                            // bill.code
                                            bill.nama_pemilik
                                            }
                                            </span>	
                                            <h5 className="uppercase font-medium text-xs text-gray-600">
                                            {
                                            // dataset.tempoh
                                            // bill.description
                                            }
                                            </h5>
                                        </div>
                                        <div className="relative w-auto pl-4 flex-initial">
                                            <h5 className="uppercase font-medium text-xs text-red-600">
                                            {
                                            // dataset.status
                                            // bill.status
                                            }
                                            </h5>
                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
                <div className="flex flex-initial flex-row-reverse pt-4 pb-4">
                <button id="type" type="button" onClick={handleSubmit} className="text-white text-center bg-green-500 flex-row-reverse rounded-full w-32 h-12">
                            {loading?'Menambah':'Tambah'}
                </button>
            </div>
            </div>
        );
    }
}
