export default function CardUser({users,loading}) {

  const [error, setError] = useState(null);
//   const [loading, setLoading] = useState(false);
//   const [user,setUser] = useState([]);
//   useEffect(() =>{
//       const fetchUsers = async () =>{
//           setLoading(true);
