import React, {useState, useEffect} from 'react'
import Footer from "./components/Footers/Footer";
import Sidebar from "./Sidebar";
import Navbar from "./components/Navbars/AdminNavbar";
import axios from "axios";
import swal from 'sweetalert';
import Carian from './Carian';

export default function Add({props}){
    const [search,setSearch] = useState({type:'',search:''});
    const [loading,setLoading] = useState(false);
    const [display,setDisplay] = useState(false);
    const [bill,setBill] = useState({
        noakaun:'',
        nama_pemilik: '',
        nokp: '',
        add_harta: ''
    });
    const searchForm = "";

    const handleSearchOption = (e) => {
        setSearch({
            ...search,
            [e.target.id]: e.target.value
        });
    }

    const handleChange = (e) => {
        // e.preventDefault();
        setSearch({
            ...search,
            [e.target.id] : e.target.value
        });
    }

    // const searchAcc = useFormInput("");
    const handleSubmit = (e) => {
        setLoading(true);
        e.preventDefault();
        axios.get('https://mymps.corrad.my/int/api_generator.php?api_name=searchBill',{
            params: {
                search:search.search,
                type:search.type
            }
        })
        .then(res => {
            let type = "";
            if(search.type === 'nokp')
                type = 'No Kad Pengenalan';
            if(search.type === 'akaun')
                type = 'Akaun';
            if(search.type === 'ssm')
                type = 'No SSM';

            console.log(res);
            if(res.data.status === 'FAILED'){
                setDisplay(false);
                swal('Tidak ditemui',type+' tidak ditemui','error');
            }
            else{
                setDisplay(true);
                setBill({
                    ...bill,
                    nama_pemilik:res.data[0].NAMA_PEMILIK,
                    noakaun:res.data[0].NOAKAUN,
                    nokp:res.data[0].NOKP,
                    add_harta:res.data[0].ADDRHARTA

                });
            }
        })
        setLoading(false);
    }

    if(search.type != ''){
        searchForm = (
            <div>
                <div className="relative bg-gray-600 md:pt-32 pt-4 pb-4">
                <form onSubmit= {(e) => handleSubmit(e)}>
                    <div className="px-4 md:px-10 mx-auto w-full">
                        <div className="flex flex-wrap">
                            <div className="w-full px-4">
                                <div className="relative flex flex-col min-w-0 break-words rounded mb-6">
                                        <div>
                                            <input aria-label="search" id="search" name="search" onChange={(e) => handleChange(e)} type="text" required className="mb-2 bg-white appearance-none rounded-none relative block w-full px-3 py-2 border border-gray-300 placeholder-gray-500 text-gray-900 rounded focus:outline-none focus:shadow-outline-blue focus:border-blue-300 focus:z-10 sm:text-sm sm:leading-5" 
                                            placeholder= {
                                                search.type == 'akaun' ? ('No Akaun') 
                                                : search.type == 'ssm' ? ('No SSM Pendaftaran Syarikat') 
                                                : search.type == 'nokp' ? ('No Kad Pengenalan') 
                                                : ('Carian...')
                                            } />
                                        </div>
                                </div>
                            </div>
                        </div>
                        <div className="flex flex-initial flex-row-reverse pt-4 pb-4">
                            <button id="type" type="submit" className="text-white text-center bg-green-500 flex-row-reverse rounded-full w-32 h-12">
                                        {loading?'Mencari..':'Cari'}
                            </button>
                        </div>
                    </div>
                </form>
                <Carian data={search} bill={bill} display={display}/>
                </div>
            </div>
        );
    }

        return (
            <div>
            <Sidebar />
            <div className="relative md:ml-64 bg-gray-400" style={{ height: "100%" }}>
                <Navbar />
                {/* Header */}
                <div className="relative bg-gray-600 md:pt-32 pt-4 pb-4">
                    <div className="px-4 md:px-10 mx-auto w-full">
                        <div className="flex flex-wrap">
                            <div className="w-full px-4">
                                <div className="relative flex flex-col min-w-0 break-words rounded mb-6">
                                    <div className="flex-auto p-4">

                                    <div className="flex flex-initial flex-row-reverse pt-4 pb-4">
                                        <button onClick={(e) => handleSearchOption(e)} id="type" value="nokp" className="text-black bg-white text-center flex-row-reverse border-solid border-2 border-gray-300 rounded-md w-full h-12">
                                                    No Kad Pengenalan
                                        </button>
                                    </div>

                                    <div className="flex flex-initial flex-row-reverse pt-4 pb-4">
                                        <button onClick={(e) => handleSearchOption(e)} id="type" value="ssm" className="text-black bg-white text-center flex-row-reverse border-solid border-2 border-gray-300 rounded-md w-full h-12">
                                                    No SSM Pendaftaran Syarikat
                                        </button>
                                    </div>

                                    <div className="flex flex-initial flex-row-reverse pt-4 pb-4">
                                        <button onClick={(e) => handleSearchOption(e)} id="type" value="akaun" className="text-black bg-white text-center flex-row-reverse border-solid border-2 border-gray-300 rounded-md w-full h-12">
                                                    No Akaun
                                        </button>
                                    </div>

                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
                    {searchForm}
                <Footer />
                </div>
            </div>
            );
}

